import React, {useState } from 'react';
import './App.css';
import DropZone from './components/DropZone';
import Grid from './components/Grid';
import board from './image/board.png';

type column = Array<string>;
type columns = Array<column>;

function App() {
  const [color, setColor] =  useState<string>('red');
  const [player, setPlayer] = useState<boolean>(true);
  const [game, setGame] = useState<string>('');
  const [columns, setColumns] = useState<columns>([
    ['','','','','',''],
    ['','','','','',''],
    ['','','','','',''],
    ['','','','','',''],
    ['','','','','',''],
    ['','','','','',''],
    ['','','','','','']
  ]);

  function getBottomCell(column_index: number) {
    const filled = columns[column_index].filter(i => i).length;
    return columns[0].length - filled - 1;
  }

  function droppedToken(column_index: number) {
    const cel =  getBottomCell(column_index);
    setColumns(preColumns => {
      const newColumns = [...preColumns];
      newColumns[column_index][cel] = color;
      return newColumns;
    });
    if (continueMatch(column_index, cel)) {
      setPlayer(prePlayer => !prePlayer);
      setColor(preColor =>  preColor === 'red' ? 'yellow' : 'red');
    }
  }

  function isMatch(column_index: number, cel_index: number) {
    if(column_index < columns.length && column_index >= 0) {
      if (cel_index < columns[0].length && cel_index >= 0) {
        console.log('valid ', column_index, cel_index)
        return columns[column_index][cel_index] === color;
      }
    } 
    console.log("invalid");

    return 0; 
  }

  function isLeftRight(column_index: number, cel_index: number) {
    let left = 0; 
    for (let i: number = 1; i <= 3; i++) {  
        if(isMatch(column_index - i, cel_index)) {
          left += 1;
        }
        else {
          i = 4;
        }
    }

    let right = 0; 
    for (let i: number = 1; i <= 3; i++) {  
        if(isMatch(column_index + i, cel_index)) {
          right += 1;
        }
        else {
          i = 4;
        }
    }

    if(left + right > 2) {
      console.log("left right", left, right);
      return true
    }
  }

  function isTopDown(column_index: number, cel_index: number) {
    let top = 0; 
    for (let i: number = 1; i <= 3; i++) {  
        if(isMatch(column_index, cel_index - 1)) {
          top += 1;
        }
        else {
          i = 4;
        }
    } 
    let down = 0; 
    for (let i: number = 1; i <= 3; i++) {  
        if(isMatch(column_index, cel_index + 1)) {
          down += 1;
        }
        else {
          i = 4;
        }
    }
    if(top + down > 2) {
      console.log("top down", top, down);
      return true
    }
  }

  function continueMatch(column_index: number, cel_index: number) {
    console.log(column_index, cel_index);

    if(isLeftRight(column_index, cel_index) || isTopDown(column_index, cel_index)) {
      setGame('Player ' + (player ? '1' : '2') + ' Won!!');
      return false;
    }
    else { 
      const empty = columns.map(column => column
        .filter(cell => !cell).length)
        .filter(i => i> 0).length > 0;

      if(!empty) {
        setGame('DRAW');
        return false;
      }  
    }

    return true;
  }

  return (
    <div className="App">
      <p>{game || 'Player' + (player === true ? '1': '2')}</p>
      <div className='board'>
        <DropZone droppedToken={droppedToken} color={color} getBottomCell={getBottomCell} columns={columns}/>
        <img className='board-img' src={board} alt="board_img"></img>
        <Grid columns={columns}/>
      </div>
    </div>
  );
}

export default App;
