import React, {useRef} from 'react';

type Props = {
    setDropping: (value: boolean) => void,
    color: string
  }  

function Token(props: Props) {
  const objectClass = 'drop-cell object yellow'
  const dropClass = `${objectClass} drop-animation ${props.color}`;
  const tokenRef = useRef<HTMLDivElement | null>(null);

  function drop(){
    props.setDropping(true);
    tokenRef.current?.setAttribute('class', dropClass);
    setTimeout(() => {
        tokenRef.current?.setAttribute('class', objectClass);
        props.setDropping(false);
    }, 4000);
  }

  return (
    <div className='drop-cell'>
         <div className="object" onClick={drop} ref={tokenRef}></div>
    </div>
  );
}

export default Token;
