import React, {useRef} from 'react';

type Props = {
    setDropping: (value: boolean) => void,
    droppedToken: () => void,
    color: string,
    getBottomCell: () => number
  }  

function Token(props: Props) {
  const objectClass = 'drop-cell object'
  const dropClass = `${objectClass} drop-animation ${props.color}`;
  const tokenRef = useRef<HTMLDivElement | null>(null);

  const useStyles = {
    '@keyframes MoveDown': {
      'from': { top: '0' }, 
      'to%': { top: 'min(24vh, 24vw)' }
    }
  }

  function drop(){
    const bottomCell = props.getBottomCell();
    console.log('bottom', bottomCell);
    if(bottomCell > -1) {
        props.setDropping(true);
        tokenRef.current?.setAttribute('class',`${dropClass} ${props.color}`);
        setTimeout(() => {
            tokenRef.current?.setAttribute('class', objectClass);
            props.setDropping(false);
            props.droppedToken();
        }, 4000);
    }
  }

  return (
    <div className='drop-cell'>
        <div className="object" onClick={drop} ref={tokenRef}></div>
    </div>
  );
}

export default Token;
